async function ajax(url,data,extension,context){let responceData=null;function getToken(name){let cookieValue=null;if(document.cookie&&""!==document.cookie){const cookies=document.cookie.split(";");for(let i=0;i<cookies.length;i++){const cookie=cookies[i].trim();if(cookie.substring(0,name.length+1)===name+"="){cookieValue=decodeURIComponent(cookie.substring(name.length+1));break}}}return cookieValue}const responce=await fetch(url,{method:"POST",mode:"cors",cache:"no-cache",headers:{"Content-Type":"application/json",Accept:"application/json","X-Requested-With":"XMLHttpRequest","X-CSRFToken":getToken("csrftoken")},body:JSON.stringify(data)}).then(responce=>responce.json()).then(data=>{responceData=data}).catch(()=>{console.log("error")});return void 0!==extension&&extension(responceData,context),responceData}