"use strict";function deliveryDate(action="get"){const days=["Monday","Tuesday","Wednesday","Thursday","Friday","Satuday","Sunday"],months=["January","February","March","April","May","June","July","August","September","October","November","December"];Date.prototype.addDays=function(days){const date=new Date(this.valueOf());return date.setDate(date.getDate()+days),date};const dateObj=new Date,date=dateObj.addDays(15),dayNum=date.getDay(),day=days[date.getDay()],month=months[date.getMonth()];if("get"==action)return{day:day,month:month,dayNum:dayNum};if("set"==action){const deliveryContainer=[...document.querySelectorAll(".delivery_date")];deliveryContainer.map(cont=>{cont.textContent=`${day}, ${month} ${dayNum}`})}}