"use strict";function switcher(inputsNames){const inputs=[...document.querySelectorAll(inputsNames)];inputs[0].checked=!0,inputs[0].parentElement.classList.add("active"),inputs.map((input,index)=>{input.addEventListener("input",()=>{inputs.map(inp=>{inp.checked=!1,inp.parentElement.classList.remove("active")}),input.checked=!0,input.parentElement.classList.add("active")})})}switcher(".switcher > div > label > input");