"use strict";function timer(timerSelector=String,resend_link=String){const resendButton=`\n        <div class="login__info w-100 d-flex align-items-center justify-content-between py-3">\n            <span>Didn't get the code ?</span>\n            <a href="${resend_link}" class="btn btn-secondary" id="resend_mail_code">Send the code again</a>\n        </div>\n    `,timer_element=document.querySelector(timerSelector);if(timer_element){const timer_parent=timer_element.parentElement;let timer_value=Number(timer_element.textContent);const timerId=setInterval(()=>{timer_value>0?timer_element.textContent=timer_value-=1:(timer_parent.insertAdjacentHTML("beforebegin",resendButton),timer_parent.remove(),timerId.remove())},1e3)}}